IF EXISTS (SELECT TABLE_NAME FROM INFORMATION_SCHEMA.TABLES WHERE TABLE_NAME='cacheTable') DROP TABLE cacheTable;
CREATE TABLE cacheTable ( Id numeric NOT NULL IDENTITY(1,1) PRIMARY KEY NONCLUSTERED, RequestString nVARCHAR(255), ExpiryDate NUMERIC NOT NULL, SheetUrl nVARCHAR(255), AbsoluteSheetUrl nVARCHAR(255), ContentType nVARCHAR(255), Xml TEXT NOT NULL,Project nVARCHAR(20),[Transaction] nVARCHAR(30));
